INSERT INTO main_note (id, section, level_id, identifier, pattern_name, main_function, main_use_when, main_note, created_at)
VALUES
    (1,'N4-L1.1.1',2,' ～が + V可能形','Ungkapan Bisa Melakukan Sesuatu','Menyatakan kemampuan, kesanggupan, atau kemungkinan bahwa subjek (yang ditandai dengan が) dapat melakukan suatu tindakan.', 'Ketika ingin menyatakan kemampuan fisik, keterampilan, atau kondisi yang memungkinkan/menghambat seseorang melakukan suatu hal.', 'Subjek untuk verba potensial biasanya ditandai dengan が, bukan は.', NOW()),
    (2,'N4-L1.1.2',2,'～ので/～なので','Pola Menyatakan Alasan (ので/なので)','Menyatakan alasan atau sebab dari suatu kondisi atau tindakan. Mirip dengan から, tetapi lebih lembut, sopan, dan cocok untuk situasi formal atau ketika berbicara dengan seseorang yang dihormati.','Dalam percakapan formal atau sopan, seperti laporan, presentasi, atau saat meminta izin. Ketika ingin memberi alasan dengan nada tidak terlalu langsung atau menyalahkan.','Jangan lupa, setelah na-adjective dan noun, harus tambah な sebelum ので → 静かなので, 学生なので', NOW()),
    (3,'N4-L1.1.3',2,'～いらっしゃいます','Pola Kehormatan (Sonkeigo)','Menyatakan "datang", "pergi", atau "ada" secara sopan tentang orang lain','Saat berbicara tentang orang yang dihormati (bos, pelanggan, guru)','Bentuk hormat dari 行く・来る・いる；subjeknya bukan pembicara sendiri',NOW()),
    (4,'N4-L1.1.4',2,'～まいります','Pola Merendahkan Diri (Kenjougo)','Menyatakan "datang" atau "pergi" secara sopan untuk diri sendiri','Saat merendahkan diri di hadapan orang yang dihormati','Bentuk sopan dari 行く・来る；digunakan oleh pembicara tentang diri sendiri atau kelompoknya',NOW()),
    (5,'N4-L1.1.5',2,'～なら、～','Pola "Kalau/Sekiranya" (〜なら)','Menyatakan kondisi atau asumsi; jika A benar, maka B','Saat memberi saran, membuat asumsi, atau menyatakan konsekuensi dari syarat','Lebih bersifat hipotesis/kontrafaktual; fokus pada reaksi terhadap A, bukan hanya sebab-akibat seperti たら／と／ば',NOW()),
    (6,'N4-L1.2.6',2,'～のが～','Menyatakan Kesan terhadap Kegiatan','Untuk menyatakan pendapat atau perasaan tentang suatu kegiatan.','Saat ingin bilang bahwa suatu kegiatan itu menyenangkan, sulit, menarik, dsb.','Fokusnya pada “kegiatan itu sendiri terasa seperti apa”.',NOW()),
    (7,'N4-L1.2.7',2,'～のは～','Menyatakan Opini terhadap Fakta','Untuk menyatakan opini atau evaluasi terhadap fakta/kegiatan sebagai topik.','Saat ingin menjelaskan alasan atau menyoroti suatu aktivitas sebagai topik utama.','Fokusnya pada “aktivitas itu sebagai topik bahasan” bukan hanya kesannya.',NOW()),
    (8,'N4-L1.2.8',2,'～ようになる','Menyatakan Perubahan pada Kemampuan','Menyatakan perubahan dari tidak bisa menjadi bisa melakukan sesuatu.','Saat ingin menceritakan perkembangan atau kemampuan baru yang didapat.','Dipakai hanya untuk hal yang awalnya sulit atau tidak bisa, lalu sekarang sudah bisa.',NOW()),
    (9,'N4-L2.1.1',2,'～そうです・～なさそうです','Kesan Penampilan (Sepertinya...)Kesan Penampilan Negatif','Menyatakan kesan atau prediksi dari apa yang dilihat / Menyatakan sesuatu tidak terlihat seperti itu.','Saat melihat sesuatu dan menebak kesannya. /Saat menebak sesuatu tidak seperti yang dibayangkan.','Hanya untuk kesan visual/situasi yang terlihat langsung.Tidak berlaku untuk semua kata sifat, hanya untuk yang punya bentuk negatif.',NOW()),
    (10,'N4-L2.1.2',2,'Vてみる','Coba Melakukan Sesuatu','Menyatakan mencoba melakukan suatu tindakan.','Saat ingin bilang sedang mencoba sesuatu.','Digunakan untuk percobaan atau pengalaman baru.',NOW()),
    (11,'N4-L2.1.3',2,'～もらえませんか','Permintaan dengan Sopan','Meminta bantuan secara sopan.','Saat minta tolong dengan hormat.','Bentuk sangat sopan, cocok untuk orang yang tidak akrab atau lebih tua.',NOW()),
    (12,'N4-L2.1.4',2,'～にくいです','Ungkapan sesuatu Sulit Dilakukan （Fokus ke Objectnya)','Menyatakan bahwa sesuatu sulit dilakukan.','Saat membicarakan kesulitan dalam melakukan aktivitas.','Subjektif—tergantung persepsi pribadi.',NOW()),
    (13,'N4-L2.1.5',2,'～やすいです','Ungkapan sesuatu Mudah Dilakukan（Fokus ke Objectnya)','Menyatakan bahwa sesuatu mudah dilakukan.','Saat membicarakan kemudahan dalam aktivitas.','Lawan dari にくいです.',NOW()),
    (14,'N4-L2.1.6',2,'～すぎます','Sesuatu Terlalu... (Berlebihan)','Menyatakan sesuatu terlalu berlebihan.','Saat sesuatu terasa lebih dari yang seharusnya.','Bisa juga dipakai dengan kata kerja bentuk ます.',NOW()),
    (15,'N4-L2.2.1',2,'～すぎる','Sesuatu Terlalu... (Berlebihan)','Menyatakan melakukan sesuatu secara berlebihan.','Saat ingin bilang kalau suatu aktivitas terlalu banyak/berlebihan.','Bisa dipakai dengan kata sifat juga (lihat pola sebelumnya).',NOW()),
    (16,'N4-L2.2.2',2,'Vてあります','Hasil Tindakan yang Sudah Ada','Menyatakan sesuatu sudah dilakukan dan hasilnya masih ada sekarang.','Saat bicara tentang keadaan yang disiapkan oleh seseorang.','Hanya dipakai untuk transitif verb (yang ada objeknya).',NOW()),
    (17,'N4-L2.2.3',2,'～と読みます','ungkapan untuk "Cara Membaca"','Menjelaskan bagaimana cara membaca sebuah kata/tulisan.','Saat menjelaskan bacaan huruf kanji/kata.','Biasanya dipakai di pelajaran atau saat bertanya tentang kanji.',NOW()),
    (18,'N4-L2.2.4',2,'～と書いてあります','ungkapan untuk "Tertulis Apa"','Menyatakan apa yang tertulis di suatu tempat/benda.','Saat menunjuk pada tulisan yang sudah ada.','Fokus pada hasil tulisan, bukan siapa yang menulis.',NOW()),
    (19,'N4-L2.2.5',2,'～にします','Memilih Sesuatu','Menyatakan memilih atau menentukan sesuatu.','Saat memutuskan sesuatu, seperti saat memesan makanan.','Sering dipakai di restoran atau saat membuat pilihan.',NOW()),
    (20,'N4-L2.2.6',2,'～はどういう意味ですか','Bertanya Arti suatu kalimat / kata','Untuk menanyakan arti dari suatu kata/kalimat.','Saat tidak tahu arti sebuah kata dan ingin bertanya.','Bentuk sangat umum dan sopan untuk bertanya arti.',NOW()),
    (21,'N4-L2.2.7',2,'～という意味です','Menjelaskan Arti suatu kalimat / kata','Untuk menjelaskan arti kata/kalimat.','Saat menjawab pertanyaan tentang arti.','Sering dipasangkan dengan pola sebelumnya (Q & A style).',NOW()),
    (22,'N4-L3.1.1',2,'[FORM 1 ] : Vた + ら、〜','Jika / Setelah','Menyatakan kondisi "jika" atau "setelah" melakukan sesuatu.','Saat ingin bicara tentang hasil dari sebuah tindakan atau kondisi setelah sesuatu terjadi.','Bisa berarti "jika" atau "setelah" tergantung konteks kalimat.',NOW()),
    (23,'N4-L3.1.2',2,'～ようと思っています','Rencana yang Sudah Dipikirkan','Menyatakan niat atau rencana yang sudah dipikirkan.','Saat menyampaikan niat yang sudah mantap dan sudah dipertimbangkan.','Bentuk ini lebih kuat dibanding “～たい” karena menunjukkan keseriusan.',NOW()),
    (24,'N4-L3.1.3',2,'～つもり','Niat Tegas','Menyatakan niat kuat untuk melakukan atau tidak melakukan sesuatu.','Untuk bicara tentang keputusan pribadi yang sudah ditetapkan.','Lebih kuat dan tegas dibanding “～たい”.',NOW()),
    (25,'N4-L3.1.4',2,'～か／～かどうか','Keadaan bingung "Apakah A atau Tidak"','Menyatakan pertanyaan tidak langsung, seperti “apakah” atau “atau tidak”.','Saat kita tidak tahu pasti atau bertanya dalam kalimat panjang.','Dipakai dalam kalimat laporan, pemikiran, atau pertanyaan tidak langsung.',NOW()),
    (26,'N4-L3.2.1',2,'~のために / ~ために','Tujuan dengan Kata Kerja & Benda','Menyatakan tujuan dari suatu tindakan. Menyatakan tujuan atau alasan yang berbasis benda atau situasi.','Saat menyebutkan alasan atau tujuan yang jelas untuk melakukan sesuatu. / Saat tujuan yang dimaksud adalah orang, benda, atau peristiwa tertentu.','Pola ini digunakan saat kamu ingin bilang “supaya...” atau “demi...” dengan kata kerja. / Digunakan saat bilang “untuk [sesuatu/seseorang]” seperti “untuk keluarga”.',NOW()),
    (27,'N4-L3.2.2',2,'~なんです / ~んです','Menjelaskan situasi atau memberi konteks.','Menjelaskan situasi atau memberi konteks atau memastikan konteks','Ini sering dipakai saat kamu ingin memastikan bahwa lawan bicara paham alasan/sebab sesuatu. / Sering dipakai untuk menekankan atau memperjelas keadaan yang sedang terjadi.','Cocok dipakai untuk menjawab “kenapa?” dengan kesan sopan dan jelas. ada 3 FORM dan jika ditanya dengan んです harus ditimpali dgn んです juga',NOW()),
    (28,'N4-L3.2.3',2,'~ければならない','Harus Melakukan Sesuatu','Menyatakan kewajiban atau keharusan.','Saat menyatakan hal yang wajib dilakukan secara formal.','Ini cara formal untuk bilang “harus”, misalnya “saya harus belajar”.',NOW()),
    (29,'N4-L4.1.1',2,'~なら','Kalimat "Jika memang begitu, ... " (Nara)','Memberikan saran atau tanggapan berdasarkan kondisi yang disebut sebelumnya.','Saat ingin memberi respons atas informasi yang baru didengar atau diketahui.','Gunakan pola ini kalau kamu ingin kasih saran setelah tahu situasinya.',NOW()),
    (30,'N4-L4.1.2',2,'~らいいです','Meminta Saran "Apa sebaiknya saya lakukan?"Memberi Saran "Sebaiknya kamu..."','Untuk bertanya apa yang sebaiknya dilakukan. / Memberi saran kepada orang lain.','Saat meminta saran atau petunjuk dari orang lain,  Saat ingin menyarankan tindakan kepada orang lain.','Pakai pola ini kalau kamu bingung dan butuh saran dari orang lain. / Cocok dipakai waktu kamu ingin menyarankan seseorang untuk melakukan sesuatu.',NOW()),
    (31,'N4-L4.1.3',2,'~といいです','Harapan/Saran Umum "Akan bagus kalau..."','Menyatakan harapan atau memberi saran secara umum.','Untuk menyampaikan harapan atau saran secara tidak langsung dan sopan.','akai pola ini kalau ingin harapanmu terdengar sopan dan tidak memaksa.',NOW()),
    (32,'N4-L4.1.4',2,'~までに','Batas Waktu "Sebelum waktu ~"','Menyatakan batas waktu suatu tindakan harus dilakukan.','Saat ingin menyebutkan tenggat waktu melakukan sesuatu.','Gunakan ini untuk menekankan bahwa sesuatu harus selesai sebelum waktu tertentu.',NOW()),
    (33,'N4-L4.2.1',2,'Vて、Vて、Vjishokei + と、(hasil/kejadian)','Urutan Kegiatan & Akibat (Kebiasaan atau Konsekuensi)','Menjelaskan bahwa jika melakukan serangkaian tindakan, maka akan terjadi sesuatu.','Saat menjelaskan rutinitas atau konsekuensi alami dari tindakan berurutan.','Pakai pola ini kalau kamu ingin jelaskan kebiasaan atau akibat yang pasti terjadi setelah beberapa tindakan.',NOW()),
    (34,'N4-L4.2.2',2,'~じゃなくて、... / ~ んじゃなくて、..','Koreksi Kata Benda (Bukan A, Tapi B).Koreksi Tindakan (Bukan Melakukan Ini, Tapi Itu)','Mengoreksi atau mengklarifikasi pernyataan dengan mengganti kata benda. / Mengoreksi atau menegaskan bahwa tindakan yang benar berbeda dari yang disangka.','Saat ingin mengatakan bahwa yang benar adalah sesuatu yang lain, bukan yang pertama disebut. / Saat ingin membetulkan kesalahan atau menyatakan tindakan yang seharusnya dilakukan.','Pakai pola ini waktu kamu ingin membetulkan bahwa "bukan A, tapi B yang benar." / Pakai ini kalau mau jelasin bahwa kamu maksudnya bukan melakukan A, tapi B.',NOW()),
    (35,'N4-L5.1.1',2,'~ しまいました / ~ しまう / ~ しまった','Menyatakan Penyesalan atau Penyelesaian','Menyatakan tindakan yang telah selesai atau terjadi tanpa sengaja (penyesalan).','Saat ingin bilang bahwa sesuatu sudah selesai atau terjadi di luar kehendak.','Pakai ini kalau kamu ingin ungkapkan penyesalan atau sesuatu yang terjadi tak disengaja.',NOW()),
    (36,'N4-L5.1.2',2,'~かもしれません','Kemungkinan (Mungkin...)','Menyatakan kemungkinan atau dugaan.','Saat kamu tidak yakin, tapi ingin menyampaikan kemungkinan.','Pakai ini kalau kamu mau bilang "mungkin..." secara sopan.',NOW()),
    (37,'N4-L5.1.3',2,'~ あとで、... / ~のあとで、...','Setelah Melakukan Sesuatu','Menyatakan urutan waktu, yaitu setelah melakukan sesuatu. / Menyatakan waktu setelah suatu kejadian (berupa kata benda).','Saat menjelaskan urutan kejadian. / Saat pakai kata benda untuk menunjukkan urutan waktu.','Pakai ini kalau mau bilang “setelah melakukan sesuatu, lalu...”. / Cocok untuk bilang "setelah (acara/kegiatan), lalu...".',NOW()),
    (38,'N4-L5.2.1',2,'~いただけませんか','Permintaan dengan Sopan','Untuk membuat permintaan dengan sangat sopan.','Saat minta tolong dengan sopan, seperti ke atasan atau orang asing.','Pakai ini saat kamu ingin minta tolong dengan sopan dan hormat.',NOW()),
    (39,'N4-L5.2.2',2,'~くて、... / ~くなくて、...','Menyatakan Sebab akibat dengan Bentuk て pada Adj-い / Adj-な / Verb','Menjelaskan sebab-akibat dengan kata sifat, dan kata kerja','etika menyebutkan alasan atau penyebab dari suatu kondisi. / Saat ingin menyebutkan bahwa karena sesuatu tidak seperti itu, maka sesuatu terjadi.','perhatikan perubahan per kata nya.',NOW()),
    (40,'N4-L5.2.3',2,'Vた + ら、...ました/でした','Ketika Terjadi (Setelah Melakukan)','Menyatakan bahwa setelah melakukan sesuatu, hasil/kejadian tertentu terjadi.','Saat menceritakan kejadian berurutan di masa lalu.','Cocok untuk cerita saat kamu bilang "setelah aku lakukan ini, ternyata begitu."',NOW()),
    (41,'N4-L6.1.1',2,'～ましょうか','Setuju dan exited (That Sound Good)','menunjukan exited pada ajakan secara sopan.','Saat ingin menawarkan diri untuk membantu seseorang. dan menunjukan exited','biasanya dipakai untuk menjawab lawan bicara karena exited',NOW()),
    (42,'N4-L6.1.2',2,'～し、～し、... / ～だし、～だし、...','Menyampaikan Pendapat & Alasan Ganda / Pendapat Alasan Ganda (Na-Kei dan Noun) ','Menyebutkan beberapa alasan/pendapat secara berurutan.','Saat menjelaskan lebih dari satu alasan. bisa disambung lebih dari 1 だし、dan lebih memberikan penilaian, saran, pendapat. untuk sambung て lebih ke umum saja.(dilatihan ubah jika ada が maka rubah jadi も)','Pakai ini kalau kamu mau bilang “soalnya A dan B juga…” / Gunakan ini kalau alasannya kata benda atau kata sifat-na.',NOW()),
    (43,'N4-L6.1.3',2,'～のは ... / ～なのは ...','Ubah Subjek ke Depan (I-kei/Verb)/(Na-Kei/Noun)','Menekankan atau menjadikan aksi/sifat sebagai subjek kalimat.','Saat ingin menonjolkan apa yang dilakukan atau sifat sebagai topik. / Ketika ingin menekankan bahwa sifat atau identitas milik siapa.','Pakai ini kalau kamu mau fokus pada "apa"-nya, bukan "siapa"-nya. / Gunakan ini kalau kamu ingin bilang “yang [sifat/identitas] itu si A.”',NOW()),
    (44,'N4-L6.1.4',2,'~そうです / ~さそうです /~くない (い/rm) + さそうです /~じゃなさそうです / ~そうにありません /~そうもありません / ~そうにもありません','Mengungkapkan suatu dugaan kuat sesuai observasi, dan bisa bisa dimplementasikan pada : +adj-い, -adj-い,  +adj-な, -adj-な, +Vます, -Vます','Menyatakan bahwa sesuatu kelihatannya begitu (dari observasi) atau tidak kelihatan seperti itu. ','Menyampaikan dugaan berdasarkan pengamatan atau perkiraan terhadap sesuatu hal','Perhatikan perubahaan nya dan ikuti form dan sesuaikan perkasus',NOW()),
    (45,'N4-L6.1.5',2,'~おきます','Persiapan / Niat untuk Keperluan Masa Depan','Menyatakan bahwa sesuatu dilakukan untuk persiapan.','Saat melakukan sesuatu agar nanti tidak repot.','Gunakan ini kalau kamu mau bilang "aku siapin dulu deh."',NOW()),
    (46,'N4-L6.1.6',2,'~あります','Persiapan / Niat untuk Keperluan Masa Depan yang sudah dilakukan','Menyatakan bahwa sesuatu telah disiapkan dan hasilnya masih terlihat/terjaga.','Saat kamu ingin menyampaikan bahwa persiapan sudah dilakukan sebelumnya dan kondisinya masih seperti itu sekarang.','Pakai ini kalau kamu ingin bilang "sudah disiapkan, dan sekarang masih begitu."',NOW()),
    (47,'N4-L７.1.1',2,'sonkeigo -  KE ORANG LAIN','Bentuk-Bentuk Honorific Special (sonkeigo) (untuk orang lain)','bentuk bentuk kata honorific yang digunakan saat pertama kali bertemu dengan orang dengan maksud sopan. bentuk ini beda sekali dari kata asal','digunakan saat berbicara dengan orang(honorific), dan diperuntukan ke orang bukan diri sendiri.','diperhatikan bentuk asal dan bentuk honorific nya, karena berbeda.',NOW()),
    (48,'N4-L７.1.2',2,'sonkeigo -  KE ORANG LAIN','Bentuk Bentuk Honorific regular (sonkeigo) (untuk orang lain)','[FORM 1] : Untuk Verb asli[FORM 2] : Untuk Verb dari kata benda ~するbentuk ini bisa diterapkan ke verb mana saja selain yang special','digunakan saat berbicara dengan orang(honorific), dan diperuntukan ke orang bukan diri sendiri.','~になる : untuk verb yang lebih abstrak atau Noun yg jadi Verb.~なさる : lebih umum digunakan, dan lebih fleksibel untu semua verb ~する.special nya : ご勉強なさるご食事なさるご仕事なさるお電話なさる',NOW()),
    (49,'N4-L７.1.3',2,'sonkeigo -  KE ORANG LAIN','Bentuk Pasif Honorific (sonkeigo) (untuk orang lain)','[FORM 1] : Untuk Verb asli[FORM 2] : Untuk Verb dari kata benda ~するbentuk ini bisa diterapkan ke verb mana saja selain yang special','Kamu bicara tentang tindakan yang dilakukan oleh orang yang lebih tinggi (atasan, dosen, klien, tamu).Kamu ingin terdengar sangat sopan atau formal, seperti di kantor, presentasi, customer service, dsb.','✅ Pasif keigo ≠ pasif biasa.> Pasif biasa menekankan objek sebagai korban.> Pasif keigo menekankan subjek sebagai orang yang dihormati.✅ Pasif keigo sering tumpang tindih dengan bentuk sonkeigo reguler.> Misalnya:   書かれました (pasif keigo) ≈ お書きになりました (sonkeigo bentuk khusus)✅ Dalam situasi sangat formal, orang lebih memilih bentuk sonkeigo reguler (お〜になる) daripada bentuk pasif.bentuk pasif humble itu gaada hanya untuk org lain aja.',NOW()),
    (50,'N4-L７.1.4',2,'Kenjougo - KE DIRI SENDIRI','Bentuk Bentuk Humble Special (Kenjougo)  (untuk diri sendiri)','Bentuk sopan dengan peruntukan diri sendiri, dengan sangat sangat sopan, dan biasa digunakan saat berbicara dengan orang yg dihormati / Merendahkan tindakan atau posisi diri sendiri (atau kelompok sendiri) demi menghormati lawan bicara.','Bicara tentang tindakan kamu sendiri kepada orang yang lebih tinggi (bos, dosen, pelanggan, dsb) / Dalam situasi formal: kantor, presentasi, email bisnis, interview kerja, dll','Kata-kata ini digunakan saat bicara tentang diri sendiri atau kelompok sendiri kepada orang yang dihormati & Kamu tidak bisa pakai ini untuk bicara tentang tindakan orang lain. ',NOW()),
    (51,'N4-L７.1.5',2,'Kenjougo - KE DIRI SENDIRI','Bentuk Bentuk Humble (Kenjougo) (untuk diri sendiri)','[FORM 1] : Untuk Verb asli[FORM 2] : Untuk Verb dari kata benda ~するBentuk sopan dengan peruntukan diri sendiri, dengan sangat sangat sopan, dan biasa digunakan saat berbicara dengan orang yg dihormati. / Merendahkan tindakan atau posisi diri sendiri (atau kelompok sendiri) demi menghormati lawan bicara.','Bicara tentang tindakan kamu sendiri kepada orang yang lebih tinggi (bos, dosen, pelanggan, dsb) / Dalam situasi formal: kantor, presentasi, email bisnis, interview kerja, dll','Kata-kata ini digunakan saat bicara tentang diri sendiri atau kelompok sendiri kepada orang yang dihormati & Kamu tidak bisa pakai ini untuk bicara tentang tindakan orang lain. ~いたします itu dipakai untuk level lebih sopan dari ~します',NOW()),
    (52,'N4-L７.2.1',2,'Adj＋く＋Verb～','Menjelaskan cara / Kondisi untuk melakukan verb','Mengubah adjektiva menjadi kata keterangan (adverb), agar bisa menerangkan atau memodifikasi kata kerja.','Saat ingin menggambarkan cara atau keadaan bagaimana suatu tindakan dilakukan.Contoh:速く走る。→ Berlari dengan cepat.美しく咲く。→ Mekar dengan indah.','Hanya berlaku untuk adj-い, bukan adj-な atau kata benda.Tidak menunjukkan perasaan atau akibat, hanya cara/keadaan.',NOW()),
    (53,'N4-L７.2.2',2,'～いるあいだに、～','"Selama sedang melakukan A, B terjadi"','Menyatakan bahwa selama kegiatan A berlangsung, kejadian atau aksi B terjadi secara sekilas / tiba-tiba / tidak berlangsung lama.','Saat ingin menggambarkan bahwa:1. Ada aksi utama yang berlangsung (A)2. Selama waktu tersebut, terjadi kejadian B yang lebih singkatContoh penggunaan:母が買い物に行っているあいだに、友だちが来ました。→ Selama ibu sedang pergi berbelanja, teman saya datang.','Jangan keliru dengan ～ているあいだ (tanpa "に"), yang menekankan bahwa dua aksi berlangsung bersamaan (bukan B terjadi tiba-tiba).Aksi B biasanya:1. Sementara (tidak berkelanjutan)2. Tiba-tiba, atau Tidak butuh waktu panjang3. Tidak cocok untuk kejadian berlangsung lama selama A.',NOW()),
    (54,'N4-L７.2.3',2,'Adj＋く＋する～','Pola Perubahan Aktif / Transformasi / Keputusan menggunakan 「～に／くする」 / ”Dibuat menjadi Noun/Adj Tersebut"','Menyatakan bahwa subjek secara aktif mengubah sesuatu menjadi X, atau memilih/memutuskan X sebagai hasil','- Untuk menunjukkan perubahan sifat/keadaan secara aktif (oleh subjek)- Dalam konteks pengambilan keputusan (memilih sesuatu)- Digunakan dalam percakapan sehari-hari dan formal','- Berbeda dengan ～になる／～くなる yang menunjukkan perubahan alami atau pasif- ～に／くする bersifat transitive (ada pelaku)- Jangan lupa bentuk adjektiva harus disesuaikan:→ Adj-い: buang い, tambah く→ Adj-な: buang な, tambah に',NOW()),
    (55,'N4-L8.1.1',2,'Vて＋あげる','Ungkapan melakukan sesuatu untuk orang lain yg posisinya tidak diatas kita (secara sosial)','Digunakan ketika subjek (pembicara atau orang lain) melakukan sesuatu untuk orang lain sebagai bentuk kebaikan atau bantuan. Fokusnya adalah pada niat baik dari pihak pemberi kepada penerima.N1→PemberiN2→PenerimaN3→Benda','Kita melakukan sesuatu untuk orang lain.Digunakan saat kita membantu orang lain dengan maksud baik. Fokusnya adalah aksi memberi bantuan.','あげる = "memberi (ke orang lain)" → digunakan saat subjek adalah pemberi, dan penerimanya adalah orang lain selain si pembicara atau keluarganya.Bisa terdengar sombong atau kurang sopan jika dipakai untuk orang yang statusnya lebih tinggi. Dalam situasi seperti itu, lebih baik pakai:～Vてさしあげる untuk lebih sopan.Hati-hati dengan konteks: jangan gunakan kepada orang lebih tua jika Anda subjeknya kecuali benar-benar dekat.',NOW()),
    (56,'N4-L8.1.2',2,'Vて+もらう','Mengungkapkan Menerima bantuan atau tindakan dari orang lain','Digunakan ketika subjek menerima kebaikan atau bantuan dari orang lain. Fokusnya ada pada penerima yang merasa diuntungkan.N1→PemberiN2→PenerimaN3→Benda','Kita menerima bantuan dari orang lain.Digunakan ketika kita diuntungkan oleh tindakan orang lain. Fokusnya adalah kita sebagai penerima bantuan.','Kalau subjek A adalah saya (watashi), bentuk ini sering menunjukkan rasa syukur atau penerimaan kebaikan.Dalam situasi formal, sering diubah menjadi bentuk sopan:～てもらいました → ～ていただきましたJika ingin sopan dan menurunkan diri (kenjōgo), bisa pakai:先生に教えていただきました。',NOW()),
    (57,'N4-L8.1.3',2,'Vて+くれる','Orang lain memberikan bantuan / tindakan kepada saya atau orang terdekat.','Digunakan ketika seseorang melakukan sesuatu untuk subjek atau orang dekat subjek sebagai bentuk bantuan atau kebaikan. Fokusnya ada pada rasa terima kasih atau penghargaan terhadap tindakan orang lain.N1→PemberiN2→PenerimaN3→Benda','Orang lain melakukan sesuatu untuk kita, dan kita merasa bersyukur atau berterima kasih.Mirip dengan もらう, tetapi ditekankan bahwa tindakan itu terasa dekat atau menyenangkan bagi kita.','Bentuk ini sering diikuti oleh ekspresi perasaan pembicara, misalnya:うれしいです (saya senang)たすかりました (sangat terbantu)ありがとう (terima kasih)Bisa juga diikuti oleh kalimat utama jika digunakan sebagai anak kalimat.',NOW()),
    (58,'N4-L8.2.1',2,'いただく（もらう・敬語）','Mengungkapkan menerima barang dari orang yang dihormati (versi sonkeigo) Bentuk Hormat "Menerima" (いただく) – Keigo Rendah Diri','Digunakan untuk menyatakan bahwa pembicara menerima sesuatu dari orang lain, dengan merendahkan diri (humble) sebagai bentuk sopan.N1→PemberiN2→PenerimaN3→BendaT→Ket Waktu','Pola ini cocok saat kita ingin menghormati lawan bicara atau orang yang memberi sesuatu, seperti atasan, guru, klien, atau orang yang lebih tua.','Gunakan “いただく” saat kamu ingin bilang “menerima sesuatu” dari orang yang dihormati, sambil merendahkan diri dengan sopan.いただく(formal)＝もらう(casual)',NOW()),
    (59,'N4-L8.2.2',2,'くださる（くれる・敬語）','Bentuk Hormat "Memberi kepada Saya" (くださる) – Keigo Menghormati Pemberi','Digunakan untuk menyatakan bahwa seseorang yang dihormati memberi sesuatu kepada pembicara (atau kelompok pembicara), dengan memuliakan si pemberi.N1→PemberiN2→PenerimaN3→Benda','Pola ini cocok saat orang yang memberi adalah orang yang lebih tinggi derajatnya (guru, atasan, orang tua), dan kita ingin menghormati mereka.','Gunakan “くださる” saat orang yang dihormati memberi sesuatu kepadamu, dan kamu ingin menunjukkannya dengan sopan.くださる(formal)＝くれる(casual)',NOW()),
    (60,'N4-L8.2.3',2,'Vていただく（してもらう・敬語）','Bentuk Hormat "Mendapat Bantuan" (Vていただく) – Keigo Rendah Diri','Digunakan untuk menyatakan bahwa pembicara menerima bantuan tindakan dari orang lain, sambil merendahkan diri sebagai bentuk sopan (humble keigo).N1→PemberiN2→PenerimaN3→Benda','Cocok digunakan saat ingin menyampaikan bahwa seseorang melakukan sesuatu untuk kita, terutama dalam konteks formal atau saat menghormati orang lain seperti atasan, guru, atau klien.','Gunakan “Vていただく” saat orang yang dihormati melakukan sesuatu untukmu, dan kamu ingin mengungkapkannya dengan rasa hormat dan rendah hatiいただく(formal)＝もらう(casual)',NOW()),
    (61,'N4-L8.2.4',2,'Vてくださる（Vてくれる・敬語）','Bentuk Hormat "Diberi Bantuan" (Vてくださる) – Keigo Menghormati Pemberi Bantuan','Digunakan saat orang yang dihormati melakukan sesuatu untuk pembicara (atau kelompoknya), dan pembicara ingin memuliakan tindakan serta si pemberi bantuan.N1→PemberiN2→PenerimaN3→Benda','Cocok dipakai saat ingin menghormati orang yang melakukan sesuatu untuk kita, seperti guru, bos, orang tua, atau klien, terutama dalam situasi sopan/formal.','Gunakan “Vてくださる” saat orang yang kamu hormati melakukan sesuatu untukmu, dan kamu ingin menunjukkan rasa hormat secara sopan.',NOW()),
    (62,'N4-L9.1.1',2,'Vfutsukei + ことになっている','Pola pengungkapan Aturan atau Keputusan yang Berlaku','Menyatakan aturan, ketentuan, atau keputusan yang telah ditetapkan.','Saat menjelaskan peraturan, rencana resmi, atau kebiasaan sistematis.','ini adalah ungkapan untuk mengungkapkan aturan tertulis/tidak yang disampaikan kembali. 	Pola ini dipakai untuk menjelaskan hal yang sudah diputuskan oleh orang lain atau sistem, bukan keputusan pribadi.',NOW()),
    (63,'N4-L9.1.2',2,'Vfutsukei + ように、「～」','Mengungkapkan kalimat Tujuan atau Harapan (mengandung kata "~Agar,~/~supaya,~")','Menyatakan tujuan atau harapan agar sesuatu terjadi (bukan niat langsung pelaku).','Digunakan saat ingin menyatakan agar/supaya sesuatu bisa terjadi secara alami, kebiasaan, atau karena usaha tidak langsung.','Dipakai ketika kita ingin sesuatu berhasil terjadi, bukan ketika kita "langsung melakukan" — jadi beda dengan 「ために」.',NOW()),
    (64,'N4-L9.1.3',2,'VJoukenkei - V条件形','Kalimat Bersyarat: "Jika / Apabila"','Menyatakan kondisi atau syarat: "Jika/Agar terjadi A, maka B" dengan bentuk verb joukenkei','Digunakan saat menyatakan syarat logis atau hipotesis: "jika ini terjadi, maka itu akan terjadi"','Pola ini cocok dipakai untuk menyatakan akibat langsung atau logis dari suatu kondisi — berbeda dari 「たら」 yang lebih fleksibel dan bisa untuk kejadian tak terduga.',NOW()),
    (65,'N4-L9.1.4',2,'Vfutsukei + ようにする','Anjuran / Usaha (Konsistensi / Kebiasaan Baru)','Menyatakan upaya atau usaha seseorang untuk melakukan sesuatu secara rutin atau sadar.','Saat ingin menyatakan kebiasaan baru, resolusi, atau sesuatu yang diusahakan dilakukan secara terus menerus.','Pola ini cocok dipakai untuk menunjukkan kesadaran/niat melakukan sesuatu sebagai rutinitas.',NOW()),
    (66,'N4-L9.2.1',2,'  sonkeigo・ご〜する・お〜する・お〜ください','Pola Kehormatan "Go-O" (ご・お) dalam Bahasa Formal (kenjougo )','Untuk menunjukkan kerendahan diri (kenjougo) saat berbicara tentang tindakan diri sendiri (FORM 1 & 2a), atau kesopanan/hormat (teineigo/sonkeigo) saat meminta orang lain melakukan sesuatu (FORM 2b).','Digunakan dalam situasi formal, seperti saat berbicara dengan klien, atasan, atau dalam layanan pelanggan, ketika ingin merendahkan diri atau menghormati lawan bicara.','Gunakan “ご〜する” dan “お〜する” saat kamu merendahkan tindakanmu, dan “お〜ください” saat sopan meminta orang lain melakukan sesuatu.',NOW()),
    (67,'N4-L9.2.2',2,'Vます(tanpaます)＋そうです','Mengungkapkan ”Kelihatannya akan ... / Hampir ...”',' Menyatakan dugaan atau prediksi sesuatu yang akan terjadi berdasarkan penampilan atau kesan saat ini','Saat kamu ingin mengungkapkan bahwa sesuatu tampak akan terjadi atau hampir terjadi, berdasarkan pengamatan langsung','Pola ini digunakan untuk menyatakan perkiraan atau kesan visual, bukan fakta pasti, masih relate dengan pola di N4-L6.1.4',NOW()),
    (68,'N4-L9.2.3',2,'Vて + おく',' Pola "Membiarkan sesuatu"','Untuk membiarkan sesuatu dalam kondisi tertentu / apa adanya','Saat kamu ingin menyampaikan bahwa kamu membiarkan sesuatu tetap seperti itu karena ada alasan atau pertimbangan','Pola ini menekankan bahwa kondisi dibiarkan begitu saja, bukan karena lupa, tapi karena memang disengaja. Perbedaan dengan pola Vて＋おきますterletak pada prefixnya, biasanya kalau pola ini awal kalimat ada kata そのまま / まだ nya. sama tergantung konteks kalimatnya',NOW()),
    (69,'N4-L9.2.4',2,'Verb Iikoukei sebagai ajakan','Mengajak seseorang dengan Verb Ikokei (next level dari Verbましょう）Pola Ajak Kasual "Yuk Lakukan..."','Mengajak seseorang dengan gaya yang lebih kasual dari ～ましょう','Saat mengajak teman dekat atau menyatakan niat sendiri dalam situasi non-formal','Pola ini lebih santai dan sering dipakai percakapan sehari-hari dibanding bentuk ～ましょう yang lebih sopan',NOW()),
    (70,'N4-L9.2.5',2,'Vて＋しまう','Pola “Menuntaskan Tindakan” / Ungkapan Melakukan sesuatu sampai selesai, sifatnya netral untuk menuntaskan / menyelesaikan','Menyatakan bahwa suatu tindakan telah diselesaikan sampai akhir','Saat ingin menegaskan bahwa suatu aktivitas dilakukan sampai tuntas, tanpa perlu menyatakan kesan positif atau negatif','Pola ini digunakan untuk menegaskan hasil akhir yang selesai',NOW()),
    (71,'N4-L9.2.6',2,' Vjisho＋ことになっている','Mengungkapkan tentang jadwal / keputusan yang ditentukan oleh orang lain. Bukan atas keinginan sendiri','Menyatakan keputusan, jadwal, atau aturan yang telah ditetapkan oleh orang lain atau oleh sistem (bukan keinginan pribadi)','Saat ingin menjelaskan keputusan yang sudah dibuat (oleh sekolah, kantor, aturan umum, dsb) dan sedang berlaku','Pola ini dipakai ketika kamu hanya menjalani atau mengikuti keputusan, bukan kamu yang menentukan sendiri',NOW()),
    (72,'N4-L10.1.1',2,'Verb ukemikei (verb pasif langsung / tidak langsung)','Mengubah Verb menjadi pasif langsung / tidak langsung','Menyatakan informasi secara pasif, dengan menjabarkan informasi pelaku. perbedaan secara makna antara pasif langusng dan tidak langsung terletak pada kejadiannya. yang paling membedakan, untuk pasif tidak langsung seperti (kaki ku terinjak), kalau pasif langusng (kaki ku diinjak) tergantung pada konteksnya','Saat ingin menyampaikan informasi secara umum, formal, atau netral (misal: pengumuman, berita).','pola ini mengubah verb menjadi pasif, biasanya seperti (dimakan, dimarahi, dimintai tolong etc.)',NOW()),
    (73,'N4-L10.1.2',2,'~のに,～ (Padahal / Meskipun)','Mengungkapkan kata "~ Padahal, ~" /  Pola 「〜のに」 untuk Ekspresi Kontras','Digunakan untuk menyatakan perasaan kontras atau sesuatu yang bertentangan dengan harapan atau fakta yang sebenarnya terjadi.','Saat ingin mengekspresikan kekecewaan, ketidaksesuaian antara harapan dan kenyataan, atau menunjukkan hal yang tidak terduga.','Gunakan 「のに」 ketika kamu ingin bilang “padahal...” atau “meskipun...” dengan nada kecewa atau heran terhadap kenyataan.',NOW()),
    (74,'N4-L10.1.3',2,'～みたい ','Pola Dugaan: みたい (kasual)','Menyatakan dugaan, kesan, atau asumsi berdasarkan pengamatan, bukan informasi pasti.','Saat kamu melihat, mendengar, atau merasakan sesuatu secara tidak langsung, dan ingin menyampaikan "sepertinya..." dalam konteks kasual maupun netral.','Gunakan みたい saat kamu merasa atau menduga sesuatu hanya dari kesan atau pengamatan, bukan dari fakta pasti—pola ini cocok untuk situasi santai sehari-hari.',NOW()),
    (75,'N4-L10.1.4',2,'~ようです','Pola Dugaan: ようです(formal)','Digunakan untuk menyatakan dugaan, kesan, atau kemungkinan berdasarkan informasi tidak langsung atau pengamatan.','Cocok digunakan saat ingin menyampaikan dugaan atau kesan secara lebih sopan, netral, atau dalam situasi formal (berita, laporan, tulisan resmi).','Gunakan 「ようです」 saat kamu ingin menyampaikan dugaan secara lebih sopan atau netral, misalnya saat berbicara dengan orang yang dihormati atau dalam situasi formal./ “Tampaknya…” atau “nampaknya…” dengan nada lebih sopan atau netral',NOW()),
    (76,'N4-L10.2.1',2,'Vjishokei + ところ','"Pola Tokoro (ところ) - Saat, Sedang, dan Selesai Melakukan Sesuatu"(selisih waktunya cuman detik-an)Mengungkapkan [FORM 1] Verb itu baru akan dilakukan[FORM 2] Verb itu sedang dilakukan[FORM 3] Verb itu baru saja selesai','Mengungkapkan tahapan waktu dari suatu tindakan: sebelum dimulai, sedang berlangsung, atau baru saja selesai dilakukan.','Digunakan saat ingin menunjukkan waktu spesifik suatu aksi dalam konteks keseharian, penjelasan, atau laporan kegiatan.','Gunakan pola ini saat ingin memberi tahu apakah kamu akan, sedang, atau baru saja melakukan sesuatu dengan cara yang jelas dan natural dalam bahasa Jepang.',NOW()),
    (77,'N4-L10.2.2',2,'Vて＋くる・いく','Mengungkapkan [FORM 1]  ... arah verb mendekat[FORM 2] ... arah verb menjauh','Kedua pola ini berfungsi untuk menyatakan arah suatu tindakan relatif terhadap pembicara atau suatu titik acuan, baik mendekat (くる) atau menjauh (いく).','Pola ini cocok dipakai saat Anda ingin menunjukkan bahwa suatu aksi atau perubahan sedang bergerak ke arah pembicara (くる) atau menjauhi pembicara (いく), baik secara fisik, temporal, atau abstrak.','Perlu diingat bahwa pola ini hanya berlaku untuk kata kerja yang menunjukkan gerakan dan secara spesifik menyatakan arah tindakan bergerak mendekat atau menjauh dari pembicara.',NOW()),
    (78,'N4-L10.2.3',2,'Adj + そうに  + Object + Verb','Mengungkapkan "Terlihat melakukan Verb dengan Ajd.."atau"Sou ni Verb atau Kelihatan Melakukan"','Pola ini berfungsi untuk menyatakan bahwa sesuatu atau seseorang terlihat melakukan suatu tindakan dengan cara tertentu, berdasarkan pengamatan visual atau kesan. Ini menggambarkan bagaimana suatu tindakan dilakukan, bukan hanya fakta bahwa tindakan itu terjadi.','Pola ini cocok dipakai ketika Anda ingin menyampaikan kesan atau dugaan Anda tentang cara seseorang atau sesuatu melakukan suatu tindakan, berdasarkan apa yang Anda lihat atau rasakan; misalnya, Anda melihat seseorang makan dengan ekspresi bahagia, Anda bisa bilang mereka "terlihat makan dengan gembira."','Jangan bingung dengan pola "〜そうだ" (terlihat/sepertinya) yang berdiri sendiri dan berfungsi sebagai prediksi umum (misalnya: 雨が降りそうだ - sepertinya akan hujan), karena pola ini secara khusus menggambarkan cara sebuah aksi dilakukan dengan sebuah kata sifat.',NOW()),
    (79,'N4-L10.2.4',2,'によって + Vukemikei','"Pasif によって" (Form 1) → Untuk pelaku terkenal/sejarah."Pasif Natural" (Form 2) → Untuk aksi umum tanpa sorotan pelaku.','Form 1: Menekankan siapa yang melakukan aksi (pelaku penting).Form 2: Menekankan objek + kondisi aksi (waktu/tempat/bahan), tanpa menyebut pelaku.','Form 1: Saat pelaku spesifik (penemu, seniman, tokoh sejarah). Contoh: "Telepon ditemukan oleh Graham Bell." Form 2: Saat pelaku tidak relevan atau aksi bersifat umum. Contoh: "Mie cup dimakan di seluruh dunia."','Form 1: Gunakan によって hanya untuk pelaku terkenal, bukan orang biasa. Form 2: Pilih partikel (で/に/から) sesuai konteks:で = tempat, に = penerima/waktu, から = bahan/asal.',NOW()),
    (80,'N4-L11.1.1',2,'~ようになる','Mulai Bisa / Terbiasa Melakukan Sesuatu','Untuk menyatakan bahwa seseorang mulai bisa atau mulai terbiasa melakukan suatu hal.','Saat menjelaskan perubahan dari tidak bisa menjadi bisa, atau dari belum terbiasa menjadi terbiasa.','Pola ini cocok untuk menyampaikan perubahan positif yang terjadi secara bertahap dalam kebiasaan atau kemampuan. bedanya dengan Materi awal N4 ada di Verbnya, kalo Verb nya jisho artinya mulai terbiasa, kalau pakai verb kanokei artinya mulai bisa terbiasa',NOW()),
    (81,'N4-L11.1.2',2,'Vなくなる','Untuk menyatakan bahwa kebiasaan lama sudah tidak dilakukan lagi.','Untuk menyatakan bahwa kebiasaan lama sudah tidak dilakukan lagi.','Untuk menyatakan bahwa kebiasaan lama sudah tidak dilakukan lagi.','Pola ini digunakan ketika ingin mengatakan "sekarang sudah tidak lagi melakukan itu."',NOW()),
    (82,'N4-L11.1.3',2,'Vたばかり','Mengungkapkan bahwa suatu aksi baru saja selesai dilakukan dalam rentang waktu dekat (beberapa jam atau hari).','Untuk menyatakan bahwa suatu tindakan atau kejadian baru saja selesai dilakukan.','Saat ingin menjelaskan bahwa sesuatu terjadi belum lama ini dan masih terasa "baru".','Gunakan pola ini kalau kamu ingin bilang "aku baru saja melakukan itu" dalam konteks waktu yang masih hangat atau dekat.',NOW()),
    (83,'N4-L11.1.4',2,'~ながら','Digunakan untuk menunjukkan bahwa dua kegiatan dilakukan dalam waktu bersamaan. (contoh : sekolah sambil partime) (rentan waktu nya besar)','Untuk menyatakan bahwa dua aktivitas dilakukan secara bersamaan, dengan aktivitas pertama sebagai latar atau kegiatan yang dilakukan sambil melakukan yang utama.','Saat menjelaskan kegiatan multitasking, seperti mendengarkan musik sambil belajar atau makan sambil menonton.','Pakai pola ini kalau kamu ingin bilang "aku melakukan A sambil melakukan B" dalam satu waktu yang bersamaan. Kegiatan utama nya disebutkan diawal atau setelah pola ながら, dan kegiatan sambilan paralelnya sebelum pola ながら',NOW()),
    (84,'N4-L11.2.1',2,'~ と言っていました','Digunakan untuk mengutip atau menyampaikan kembali apa yang dikatakan orang lain.','Untuk melaporkan atau menyampaikan kembali ucapan atau pendapat orang lain secara tidak langsung.','Saat ingin memberi tahu orang lain tentang apa yang dikatakan seseorang sebelumnya.','Pola ini berguna saat kamu mau bilang “dia bilang begini” tanpa harus mengutip secara langsung.',NOW()),
    (85,'N4-L11.2.2',2,'Verb Meireikei','Aplikasikan meireikei atau bentuk memerintah langsung','Digunakan untuk memberikan perintah langsung kepada orang lain agar melakukan suatu tindakan.','Dalam situasi kasual atau otoritatif, seperti:Antara teman dekat (dengan hati-hati).Atasan kepada bawahan.Guru kepada murid.Dalam situasi darurat (misal: 「逃げろ！」– “Larilah!”).Di rambu-rambu, instruksi tertulis, atau manual.','Pola ini terdengar kasar atau memaksa, jadi jangan digunakan sembarangan—lebih baik pakai bentuk perintah sopan seperti ～てください dalam situasi formal atau netral.',NOW()),
    (86,'N4-L11.2.3',2,'~ながら','Digunakan untuk menunjukkan bahwa dua kegiatan dilakukan dalam waktu bersamaan. (contoh : nonton sambil main) (rentan waktu nya kecil)','Untuk menyatakan bahwa dua aktivitas dilakukan secara bersamaan, dengan aktivitas pertama sebagai latar atau kegiatan yang dilakukan sambil melakukan yang utama.','Saat menjelaskan kegiatan multitasking, seperti mendengarkan musik sambil belajar atau makan sambil menonton.','Pakai pola ini kalau kamu ingin bilang "aku melakukan A sambil melakukan B" dalam satu waktu yang bersamaan. Kegiatan utama nya disebutkan diawal atau setelah pola ながら, dan kegiatan sambilan paralelnya sebelum pola ながら',NOW()),
    (87,'N4-L12.1.1',2,'[Hitungan]も','Pola yg menunjukan jumlah yg melebihi ekspetasi','Untuk menyatakan bahwa jumlah sesuatu melebihi harapan atau ekspektasi, menekankan kejutan atau ketidakterdugaan.','Saat ingin menunjukkan bahwa jumlah atau frekuensi suatu hal terasa banyak atau tidak biasa.','Gunakan pola ini saat kamu ingin membuat pendengar merasa terkejut atau memberi penekanan bahwa “sebanyak itu loh!” dalam konteks yang tidak biasa.',NOW()),
    (88,'N4-L12.1.2',2,'[Hitungan]しか  / [Object]しか','Pola yg menunjukan Jumlah Terbatas / Kurang dari yang Diharapkan','Untuk menyatakan bahwa jumlah atau pilihan sangat terbatas, biasanya diikuti kata kerja bentuk negatif.','Saat ingin menunjukkan bahwa sesuatu hanya sedikit atau tidak cukup, mengekspresikan rasa kekurangan.','Gunakan pola ini ketika kamu ingin menyampaikan bahwa “cuma segini loh…” dengan nuansa kecewa atau keterbatasan.',NOW()),
    (89,'N4-L12.1.3',2,' Vた + ら どうですか','Memberi Saran atau Rekomendasi','Untuk memberikan saran atau anjuran secara sopan, seperti mengatakan "bagaimana kalau...?"','Saat ingin menyarankan sesuatu dengan nada lembut atau tidak memaksa kepada orang lain.','Gunakan pola ini saat kamu ingin menyarankan sesuatu dengan sopan, seolah berkata “coba deh, gimana kalau…?”',NOW()),
    (90,'N4-L12.1.4',2,'"Tanpa Melakukan Itu" – Pola ないで untuk Larangan & Saran','Larangan atau Saran Menghindari Sesuatu','Untuk memberi saran agar tidak melakukan sesuatu, atau menunjukkan bahwa suatu tindakan dilakukan tanpa melakukan tindakan lainnya terlebih dahulu.','Saat ingin menyarankan untuk menghindari suatu tindakan atau menjelaskan bagaimana sesuatu dilakukan tanpa disertai tindakan lain.','Gunakan pola ini saat kamu ingin bilang "lebih baik jangan lakukan itu" atau "lakukan ini tanpa melakukan itu" dengan cara yang halus dan jelas.',NOW()),
    (91,'N4-L12.1.5',2,'～らしい ','Perkiraan atau Karakteristik Berdasarkan Informasi Umum','Digunakan untuk menyatakan dugaan berdasarkan informasi yang didengar (tidak pasti), atau menyatakan sesuatu yang mewakili ciri khas atau idealnya sesuatu.','Cocok saat ingin menyampaikan informasi tidak langsung (seperti gosip, berita) atau menilai sesuatu dari kesan umum atau idealnya.','Pakai pola ini saat kamu ingin menyampaikan “kayaknya…” atau “memang kelihatan seperti itu” berdasarkan apa yang kamu dengar atau persepsikan.kalimat sebleumnya bisa dicombo dengan [Objectを + Vjishoと, ~] atau langsung pernyataan seperti [Objectは ~ ]',NOW()),
    (92,'N4-L12.1.6',2,'Cara atau Keadaan Saat Melakukan Tindakan','"Dengan atau Tanpa?" – Pola Menyatakan Cara Melakukan Sesuatu[FORM 1] :  ~ Dengan ~[FORM 2] :  ~ Tanpa ~','Untuk menjelaskan bagaimana suatu tindakan dilakukan, apakah disertai (dengan) atau tanpa melakukan hal lain.','Cocok digunakan saat ingin memberi tahu cara, kondisi, atau alat saat melakukan sesuatu — apakah sesuatu digunakan, dilakukan, atau justru dihindari.','Pakai pola ini saat kamu ingin menjelaskan “dengan apa” atau “tanpa apa” kamu melakukan suatu tindakan secara sederhana dan jelas.',NOW()),
    (93,'N4-L12.2.1',2,'~のに [Other Verb] / ～に [Other Verb]','Menunjukan kegunaan / fungsi suatu object','Untuk menyatakan kegunaan atau tujuan dari suatu benda atau tindakan, seperti “benda ini digunakan untuk...” atau “bermanfaat untuk...”.','Cocok dipakai saat ingin menjelaskan fungsi alat, objek, atau kegiatan, terutama dalam konteks instruksi, penjelasan teknis, atau percakapan sehari-hari.','Gunakan pola ini saat kamu ingin bilang “ini dipakai buat...” dengan cara yang alami dan informatif dalam bahasa Jepang.',NOW()),
    (94,'N4-L12.2.2',2,'~やすい / ~にくい','Ungkapan sesuatu Mudah / Sulit Dilakukan （Fokus ke Subject nya)','Digunakan untuk menyatakan bahwa suatu tindakan mudah atau sulit dilakukan, baik karena kondisi fisik, teknis, atau kebiasaan.','Cocok saat ingin menjelaskan kenyamanan, kebiasaan, atau tantangan dalam melakukan suatu hal, seperti dalam percakapan sehari-hari atau instruksi.','Gunakan pola ini saat kamu ingin bilang "gampang dilakuin" atau "susah banget nih" secara natural dan praktis dalam bahasa Jepang.bedanya dengan pola sebelumnya, ini lebih fokus ke kondisi',NOW()),
    (95,'N4-L12.2.3',2,'Vjisho/ない +  ようにしている','Kebiasaan yg diusahakan secara sadar','Untuk menyatakan bahwa seseorang berusaha secara sadar untuk membiasakan diri melakukan atau tidak melakukan sesuatu.','Cocok dipakai saat ingin menjelaskan rutinitas atau kebiasaan yang dilakukan dengan kesadaran penuh, bukan kebiasaan otomatis.','Pakai pola ini saat kamu ingin bilang "aku sengaja ngebiasain ini" — bukan karena kebetulan, tapi karena niat dan usaha pribadi.',NOW()),
    (96,'N4-L12.2.4',2,'Vjisho/ない +  ことにする','Mengungkapkan tentang jadwal / keputusan yang ditentukan oleh diri sendiri . Bukan atas keinginan orang lain','Menyatakan keputusan, jadwal, atau aturan yang telah ditetapkan oleh diri sendiri (bukan orang lain / sistem)','Saat ingin menjelaskan keputusan yang sudah dibuat (oleh diri sendiri) dan sedang berlaku','Pola ini dipakai ketika kamu hanya menjalani atau mengikuti keputusan yg sudah dibuat diri sendiri',NOW()),
    (97,'N4-L13.1.1',2,'Verb siekikei','Pola "orang Disuruh Melakukan Sesuatu oleh orang lain" (Kousuikei/Perintah)','Menyatakan bahwa N1 menyuruh/memaksa N2 untuk melakukan suatu tindakan.N1 : yg memerintahN2 : yang diperintah','Saat menjelaskan siapa menyuruh siapa melakukan apa, baik dalam kalimat naratif atau instruksi.','Pola ini dipakai kalau kamu ingin bilang “si A menyuruh si B melakukan sesuatu” dengan sopan atau netral.',NOW()),
    (98,'N4-L13.1.2',2,'～のを見た・のを見なかった','Melihat orang melakukan sesuatu',' Menyatakan bahwa kita melihat (atau tidak melihat) seseorang melakukan suatu tindakan.','Digunakan saat melaporkan kejadian yang disaksikan langsung, baik yang sedang terjadi, sudah terjadi, atau rencana.','Gunakan pola ini kalau kamu ingin bilang "aku lihat dia sedang / sudah / akan melakukan sesuatu."',NOW()),
    (99,'N4-L13.1.3',2,'~Vて + いた','Sedang & Sudah Pernah Melakukan (Vている / Vていた)','Menyatakan aktivitas yang sedang berlangsung (ている) atau sedang berlangsung di masa lalu (ていた)','Saat menggambarkan apa yang sedang terjadi, atau apa yang sedang dilakukan pada waktu tertentu di masa lalu.','bedanya ころ(waktu nya perkiraan)  dengan とき(waktu nya pasti)Pakai Vている kalau kejadian masih berlangsung sekarang, dan Vていた kalau itu sedang berlangsung waktu dulu.',NOW()),
    (100,'N4-L13.2.1',2,'～Vてばかり','Mengungkapkan "Kebiasaan Berlebihan"','Mengungkapkan bahwa seseorang melakukan satu aktivitas secara berlebihan atau terus-menerus (dan sering kali dengan kesan negatif atau tidak seimbang).','Digunakan saat ingin mengkritik, menyindir, atau menunjukkan kebiasaan yang tidak ideal karena terlalu terfokus pada satu hal saja.','Pola ini cocok dipakai saat kamu ingin bilang seseorang “kerjaannya itu-itu terus” secara berlebihan, biasanya buat menegur hal yang kurang baik.',NOW()),
    (101,'N4-L13.2.2',2,'Verb siekiukemeikei','Pola Pasif-Kausatif – “Terpaksa Melakukan Sesuatu”Dipaksa melakukan suatu pekerjaan dan ada persaan kurang senang karena dipaksa','Mengungkapkan bahwa seseorang dipaksa untuk melakukan suatu tindakan oleh pihak lain — ada unsur paksaan atau tidak sukarela.','Digunakan saat ingin menyatakan bahwa seseorang melakukan sesuatu bukan atas kemauannya sendiri, melainkan karena disuruh atau dipaksa oleh orang lain.','Pakai pola ini kalau kamu mau bilang “aku disuruh dan nggak bisa nolak,” alias merasa terpaksa ngelakuin sesuatu.',NOW()),
    (102,'N4-L13.2.3',2,'Vsiekikei(formて) + くれる・もらう','[FORM 1] : Mengizinkan[FORM 2] : Mendapatkan kesempatan','Digunakan untuk menyatakan bahwa kita diizinkan atau diberi kesempatan melakukan sesuatu oleh orang lain (dengan nuansa rasa syukur atau penerimaan).N1　→　PemberiN2　→　Penerima','Dipakai saat ingin menyampaikan bahwa kita diperbolehkan melakukan sesuatu atas kebaikan orang lain, seperti guru, atasan, atau orang tua — sering digunakan dalam situasi formal atau penuh rasa hormat.','Pakai pola ini kalau kamu mau bilang “Aku dibolehin ngelakuin sesuatu sama orang lain, dan aku bersyukur atas itu.bentuk te nya itu ます nya langsung jadiて saja seperti grup 2 karena hasil dari perubahan Vsiekikei itu ~せます (seperti verb2)',NOW()),
    (103,'N4-L14.1.1',2,'A というのは B です','Menjelaskan suatu istilah','Untuk menjelaskan pengertian, definisi, atau makna sebuah istilah, konsep, atau hal supaya lawan bicara paham.A = istilah atau konsep yang ingin dijelaskanB = penjelasannya (definisi, fungsi, makna)','>Menjelaskan arti sebuah kata/istilah baru>Memberikan definisi>Memberikan penjelasan supaya orang lain mengerti maksudnyaBiasanya muncul di percakapan formal, penjelasan di buku, pidato, dan sebagainya.','✅ A biasanya berupa kata benda atau istilah (bisa + kata kerja bentuk biasa)✅ Jangan lupa setelah 「というのは」 selalu ada penjelasannya (B)✅ Lebih cocok dipakai dalam kalimat yang agak formal atau saat menjelaskan sesuatu✅ Jika ingin lebih kasual, bisa pakai 「って」:例: 成人式って 20歳になったことを祝う行事だよ。',NOW()),
    (104,'N4-L14.1.2',2,'N + って何ですか','Menanyakan suatu istilah','Untuk menanyakan arti atau maksud dari sebuah kata atau istilah secara langsung.','Saat kamu ingin tahu arti kata atau istilah yang baru kamu dengar atau tidak kamu pahami.','Pola ini sangat umum dan santai, cocok dipakai dalam percakapan sehari-hari ketika kamu ingin bertanya "Itu artinya apa, sih?"',NOW()),
    (105,'N4-L14.1.3',2,'~と言われています','"Kata Orang Jepang" Formula(Agar mudah diingat bahwa ini pola menyampaikan apa yang umum dikatakan orang-orang.)','Untuk menyampaikan opini atau kepercayaan umum yang diyakini banyak orang, tanpa menyatakan pendapat pribadi.','Saat menulis atau berbicara dalam konteks formal, penjelasan budaya, atau saat ingin menghindari subjektivitas.','Pakai pola ini kalau kamu mau menyampaikan sesuatu yang "katanya begitu", tanpa menyebut siapa yang bilang.',NOW()),
    (106,'N4-L14.1.4',2,'~ のような~','Pola Perbandingan "N2 yang seperti N1"','Untuk menyatakan bahwa N2 memiliki sifat atau bentuk yang menyerupai N1.','Saat ingin menjelaskan kemiripan antara dua benda, orang, atau kondisi secara deskriptif, terutama dalam tulisan naratif, deskripsi, atau ekspresi keinginan.','Pakai pola ini kalau kamu mau bilang sesuatu itu “kayak” atau “mirip” sesuatu yang lain, tapi bukan benar-benar sama.',NOW()),
    (107,'N4-L14.2.1',2,'「〜かな(あ)」「〜ようかな(あ)」','[FORM 1 - 5 ] Pola berpikir / bertanya (keraguan ringan atau ide) [FORM 6] Pola membalas untuk "gimana kalau N..."','Mengungkapkan pikiran yang belum pasti, seperti:"Kira-kira gimana ya...""Mau ngapain ya...""Mungkin begitu ya..."','Saat berbicara sendiri (monolog), ngobrol santai, atau mengungkapkan pikiran ringan kepada teman — bukan kalimat tanya langsung ke lawan bicara.','Pola ini cocok untuk ngomong santai sambil mikir sendiri atau saat kamu belum yakin, kayak ngomong dalam hati: "hmm, gimana yaa~".',NOW()),
    (108,'N4-L14.2.2',2,'~がっている','Untuk menggambarakn perasaan orang lain "si A sedang merasa ...."','Untuk menyatakan perasaan, keinginan, atau reaksi emosional orang lain, bukan diri sendiri.','Saat menggambarkan apa yang dirasakan atau diinginkan oleh orang ketiga, baik dalam narasi, cerita, atau laporan pengamatan.','Pakai pola ini kalau kamu mau bilang "dia lagi pengin/merasa apa" — tapi bukan untuk ngomongin dirimu sendiri ya!',NOW()),
    (109,'N4-L14.2.3',2,'Vfutsukei + からです','Menjelaskan / Menyatakan alasan secara jelas','Untuk menjelaskan atau menegaskan alasan secara langsung dan sopan, terutama sebagai jawaban dari pertanyaan "kenapa?"','Digunakan saat menjawab pertanyaan, menjelaskan sebab akibat, atau memberi klarifikasi secara logis dalam percakapan formal maupun informal.','Pakai pola ini kalau kamu mau bilang “karena...” dengan nada jelas dan sopan biar alasanmu gampang dipahami.',NOW()),
    (110,'N4-L15.1.1',2,'Vfutsukei + んだって','Ungkapan tidak langsung versi kasual','んだけど: membuka pembicaraan, menyatakan situasi, atau memberi petunjuk akan ada lanjutan.んだって: menyampaikan info yang didengar dari orang lain (tidak langsung).','Saat ngobrol santai dengan teman, memberi konteks sebelum bertanya/meminta sesuatu, atau mengulang info dari orang lain.',' Pola ini bikin omonganmu terdengar lebih natural dan nyambung saat ngobrol santai, apalagi pas mau bilang “katanya…” atau pas mau mulai cerita sesuatu.',NOW()),
    (111,'N4-L15.1.2',2,'Vjisho + のはどう？','Usulan, ajakan halus (kasual)','Menyatakan saran atau ide dengan nada ringan dan tidak memaksa.','Saat ngobrol santai dengan teman, pas ingin ngajak, ngasih ide, atau menyarankan sesuatu secara halus dan nggak menggurui.','Pola ini enak dipakai kalau mau nyaranin sesuatu ke temen tanpa terkesan nyuruh, jadi lebih akrab dan natural.',NOW()),
    (112,'N4-L15.1.3',2,'~だけじゃなくて~','"bukan hanya melakukan Act A, sebaiknya bagaimana kita melakukan Act B juga?"','Menyatakan bahwa tidak hanya melakukan hal A, tetapi juga melakukan hal B.','Saat ingin menekankan bahwa suatu tindakan tidak terbatas pada satu hal saja, tapi mencakup lebih dari itu.','Gunakan pola ini kalau kamu ingin bilang “nggak cuma A lho, kita juga harus B!”',NOW()),
    (113,'N4-L15.1.4',2,'~ないと','“~ないと” sebagai Bentuk Konsekuensi Jika Tidak..."Jika bukan... / Jika tidak...."','Menyatakan konsekuensi negatif atau peringatan jika suatu kondisi tidak terpenuhi.','Saat ingin menegaskan bahwa hal tertentu harus dilakukan atau harus benar, jika tidak maka akan ada akibatnya.','Gunakan pola ini untuk menyampaikan peringatan atau keharusan, seperti “kalau nggak gini, nanti masalah.”',NOW()),
    (114,'N4-L15.2.1',2,'Vて + きますた','Perubahan yg terjadi dari dulu ke sekarang','Mengungkapkan perubahan atau perkembangan yang telah terjadi dari masa lalu dan masih berlanjut sampai sekarang.','Cocok dipakai saat menjelaskan suatu proses perubahan, pertumbuhan, atau perkembangan yang terus berlangsung sampai waktu kini.','Gunakan pola ini saat kamu ingin menunjukkan bahwa sesuatu telah berubah secara bertahap dari dulu hingga sekarang.',NOW()),
    (115,'N4-L15.2.2',2,'～ために,~','"Sebab-Akibat Logis: ～ために"','Digunakan untuk menyatakan sebab-akibat yang logis dan jelas, di mana “ために” menunjukkan alasan atau penyebab yang bersifat formal.','Cocok dipakai saat kamu ingin menjelaskan alasan atau penyebab suatu tindakan secara tertata dan formal, misalnya dalam tulisan, laporan, atau situasi akademik.','Gunakan pola ini saat kamu ingin menjelaskan alasan yang kuat dan masuk akal secara formal, bukan sekadar sebab emosional atau spontan.',NOW()),
    (116,'N4-L15.2.3',2,'Vて + いきます','Perubahan yg akan berlanjut kemasa depan','Menyatakan bahwa suatu aksi atau kondisi akan berlanjut dari sekarang ke masa depan.','Saat ingin menjelaskan rencana berkelanjutanUntuk menyatakan perubahan bertahapSaat menggambarkan perkembangan atau kebiasaan yang akan terus berlangsung','Gunakan pola ini saat ingin bilang kalau sesuatu akan dimulai sekarang dan terus berlangsung ke depan.',NOW());
